screen.userinput:
cat > perl_wrapper.c << "EOF"
#include <stdio.h>
#include <stdlib.h>
#include <string.h>
#include <unistd.h>
#include <errno.h>

int main (int argc, char *argv[])
{
  char *perl_arch;
  if ((perl_arch = getenv("PERL_ARCH")) == NULL)
    perl_arch = "64";

  char *filename = malloc(strlen(argv[0]) + strlen(perl_arch) + 2);
  strcpy(filename, argv[0]);
  strcat(filename, "-");
  strcat(filename, perl_arch);

  int ret = 0;
  ret = execvp(filename, argv);
  if ((ret != 0)&&(errno != 0))
  {
    char *errmsg = malloc(strlen(filename) + 19);
    strcpy(errmsg, "Unable to execute ");
    strcat(errmsg, filename);
    perror(errmsg);
    free(errmsg);
  }

  free(filename);

  return ret;
}
EOF
gcc ${BUILD64} perl_wrapper.c -o /usr/bin/perl
